`,(0,e.jsxDEV)(a.pre,{children:(0,e.jsxDEV)(a.code,{className:"language-jsx",children:`// Next.js App Router example
"use client";

import {YooUIProvider, SupportedCalendars} from "@simao234430/react";
import {CalendarDate, GregorianCalendar} from "@internationalized/date";

export interface ProvidersProps {
  children: React.ReactNode;
}

function createCalendar(identifier: SupportedCalendars) {
  switch (identifier) {
    case "gregory":
      return new GregorianCalendar();
    default:
      throw new Error(\`Unsupported calendar \${identifier}\`);
  }
}

export function Providers({children}: ProvidersProps) {
  const router = useRouter();

  return (
    <YooUIProvider
      locale="hi-IN-u-ca-indian"
      defaultDates={{
        minDate: new CalendarDate(1900, 1, 1),
        maxDate: new CalendarDate(2099, 12, 31),
      }}
      createCalendar={createCalendar}
    >
      {children}
    </YooUIProvider>
  );
}
`},void 0,!1,{fileName:"/Users/simao/xiaosimao123/yooui/apps/docs/content/blog/_mdx_bundler_entry_point-a69d5543-9053-4d62-b53c-54dc7d1d1e53.mdx",lineNumber:257,columnNumber:1},this)},void 0,!1,{fileName:"/Users/simao/xiaosimao123/yooui/apps/docs/content/blog/_mdx_bundler_entry_point-a69d5543-9053-4d62-b53c-54dc7d1d1e53.mdx",lineNumber:257,columnNumber:1},this),`
